<template>
  <div class="login-view">
    <h1>
      <a href="javascript:history.go(-1);">取消</a>登录豆瓣
    </h1>
    <form method="get" @submit.prevent="onSubmit()">
      <p v-if="error" class="tip error">{{error}}</p>
      <div class="form-user">
        <label>
          <strong>邮箱</strong>
          <input
            v-model="email"
            type="email"
            name="email"
            placeholder="邮箱">
        </label>
      </div>
      <div class="form-pwd">
        <label>
          <strong>请输入密码</strong>
          <template v-if="passType === 'password'">
            <input
              v-model="token"
              type="password"
              name="token"
              placeholder="Token">
          </template>
          <template v-if="passType === 'text'">
            <input
              v-model="token"
              name="token"
              type="text"
              placeholder="Token">
          </template>
          <span class="show-pwd" :class="{show: isShow}" @click="showPwd()"></span>
        </label>
      </div>
      <div class="">
        <button
          type="submit"
          class="submit"
          :disabled="isDisabled"
          :class="{disabled: isDisabled}">
          {{loginState}}
        </button>
      </div>
    </form>
    <div class="footer">
      <div class="more-login">使用其他方式登录 &amp; 找回密码</div>
      <div class="btns">
        <router-link :to="{name: 'RegisterView'}">注册豆瓣账号</router-link>
        <a href="#">下载豆瓣App</a>
      </div>
    </div>
  </div>
</template>

<script>
  import { mapState } from 'vuex'
  export default {
    name: 'login-view',
    data () {
      return {
        loginState: '登录',
        isDisabled: false,
        isShow: 0,
        passType: 'password',
        error: ''
      }
    },
    computed: {
      ...mapState({
        email: state => state.user.login_email,
        token: state => state.user.login_token,
        name: state => state.user.login_name
      })
    },
    methods: {
      showPwd () {
        this.isShow = this.isShow ? 0 : 1
        this.isShow ? this.passType = 'text' : this.passType = 'password'
      },
      beforeSubmit () {
        this.isDisabled = true
        this.loginState = '正在登录...'
      },
      onSuccess (res) {
        this.$router.push({name: 'HomeView'})
      },
      onError () {
        this.error = err.body.error
        this.loginState = '登录'
        this.isDisabled = false
      },
      onSubmit () {
        this.beforeSubmit()

        this.$state.dispatch({
          type: 'login',
          email: this.email,
          token: this.token
        }).then(res => {
          this.onSuccess(res)
        }, err => {
          this.onError(err)
        })
      }
    },
    created () {
     this.$store.commit({
       type: 'getLocalUser'
     })
    }
  };
</script>

